import React, { useState, useEffect } from 'react';

const API_URL = 'http://localhost:8000/api/auth/getuser';

const Profile = () => {
  const [user, setUser] = useState({
    Name: '',
    Email: '',
    Gender: '',
  });

  // Function to fetch and update user information
  const fetchUserInfo = async () => {
    try {
      const response = await fetch(API_URL, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`, // assuming token is stored in localStorage
        },
      });

      if (response.ok) {
        const userData = await response.json();
        setUser({ ...userData });
      } else {
        console.error('Response not ok:', response.status, response.statusText);
      }
    } catch (error) {
      console.error('Error fetching user information', error);
    }
  }

  useEffect(() => {
    fetchUserInfo();
    // Optionally, return a cleanup function here if needed
  }, []);

  // Update user gender in state
  const handleGenderChange = (event) => {
    setUser(prev => ({ ...prev, Gender: event.target.id }));
  };

  return (
    <>
      <h2>Your Profile</h2>
      <div>
        <h6>Name: {user.Name}</h6>
        <h6>Email: {user.Email}</h6>
        <div className="container">
          <h6>Gender:</h6>
          <input
            className="form-check-input mt-3 mx-2"
            type="radio"
            name="gender"
            id="male"
            checked={user.Gender === 'male'}
            onChange={handleGenderChange}
          />
          <label className="form-check-label mt-3 mx-2" htmlFor="male"><b>Male</b></label>
          <input
            className="form-check-input mt-3 mx-2"
            type="radio"
            name="gender"
            id="female"
            checked={user.Gender === 'female'}
            onChange={handleGenderChange}
          />
          <label className="form-check-label mt-3 mx-2" htmlFor="female"><b>Female</b></label>
        </div>
      </div>
    </>
  );
};

export default Profile;
